function formatCurrencyLimit(htmlString) {
  if (!htmlString) return '';

  // Decode HTML entities
  const textarea = document.createElement('textarea');
  textarea.innerHTML = htmlString;
  let decoded = textarea.value;

  // Additional manual replacements for common entities
  decoded = decoded.replace(/&euro;/gi, '€').replace(/&pound;/gi, '£').replace(/&dollar;/gi, '$').replace(/&amp;/gi, '&').replace(/&lt;/gi, '<').replace(/&gt;/gi, '>').replace(/&quot;/gi, '"').replace(/&#39;/gi, "'");

