  /**
   * Utility method to convert frontend data to Terracotta format
   */
  static convertToTerracottaFormat(formData: any, availablePolicyTypes?: TerracottaPolicyType[]): TerracottaQuoteRequest {
    console.log('Converting form data to Terracotta format:', formData);

    // Convert travelers to Terracotta format using actual user data
    const travelers: TerracottaTraveler[] = formData.travelers.map((traveler: any, index: number) => {
      // Prioritize date of birth over age for accuracy
      const dateOfBirth = traveler.dateOfBirth || calculateDateOfBirth(parseInt(traveler.age));
      const calculatedAge = traveler.dateOfBirth ? 
        calculateAgeFromDateOfBirth(traveler.dateOfBirth) : 
        parseInt(traveler.age);

      console.log(`Traveler ${index + 1}: DOB=${dateOfBirth}, Age=${calculatedAge}`);

      return {
        TravellerNumber: index + 1,
        Title: traveler.title || 'Mr', // Use actual title from form
        FirstName: traveler.firstName,
        LastName: traveler.lastName,
        DateOfBirth: formatDateForTerracotta(dateOfBirth),
        Age: calculatedAge,
        AgebandID: getAgebandID(calculatedAge),
        TitleID: getTitleID(traveler.title || 'Mr'),
        minAge: calculatedAge,
        maxAge: calculatedAge,
        ScreeningInformation: null
      };
    });

    // Convert contact details using actual user data
    const contactDetails: TerracottaContactDetails = {
      Address: formData.billingAddress?.street || formData.contactAddress?.street || '123 Main Street',
      Postcode: formData.billingAddress?.postalCode || formData.contactAddress?.postalCode || '12345',
      Email: formData.travelers[0]?.email || formData.contactEmail || 'test@example.com',
      Telephone: formData.travelers[0]?.phone || formData.contactPhone || '+30123456789',
      VehicleMakeModel: formData.vehicleMakeModel,
      VehicleLicensePlate: formData.vehicleLicensePlate,
      VehicleYearManufacture: formData.vehicleYearManufacture
    };

           // Convert trip details using actual user data
           const quoteDetails: TerracottaQuoteDetails = {
             ResidenceID: getResidenceID(formData.countryOfResidence),
             TypePolicyID: getTypePolicyID(formData.tripType, availablePolicyTypes),
             TypePackageID: '1', // Fixed value as requested (string)
             Destination: formData.destination || 'Europe',
             StartDate: formatDateForTerracotta(formData.startDate),
             EndDate: formatDateForTerracotta(formData.endDate),
             Travellers: travelers,
             ContactDetails: contactDetails,
             includeAnnualQuotes: formData.tripType === 'annual' ? 1 : 0,
             includeUpsell: 0, // Simplified for now
             currencyID: 1, // EUR
             schemaIDFilter: '717' // Use schema 717
           };

    console.log('Converted Terracotta request:', quoteDetails);

    // Use fixed values as requested
    return {
      userID: '4072',
      userCode: '111427',
      quoteDetails
    };
  }
