                  value={traveler.age}
                  onChange={(e) => handleTravelerChange(index, 'age', e.target.value)}
                  placeholder="Enter age"
                  min="1"
                  max="120"
                  required
                />
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-group">
                <label>Email</label>
                <input
