  firstName: string;
  lastName: string;
  age: string;
  email: string;
  phone: string;
  vaxId: string;
  nationality: string;
}

interface QuoteOption {
  id: string;
  name: string;
  type: 'basic' | 'standard' | 'premium';
  price: number;
  coverage: {
    medical: string;
    baggage: string;
    cancellation: string;
    activities: string[];
  };
  features: string[];
  // Terracotta specific fields
  terracottaQuoteId?: string;
  schemaName?: string;
  policyTypeName?: string;
  summaryOfCoverUrl?: string;
  policyWordingUrl?: string;
  currency?: string;
  ipt?: number;
}

