      case 2:
        return !!formData.selectedQuote;
      case 3:
        return true; // Additional policies phase is always valid (optional)
      case 4:
        return true; // Summary phase is always valid
      case 5:
        return true; // Confirmation phase is always valid
      case 6:
        // Screening questions phase - all questions must be answered
        return screeningQuestions.length === 0 || Object.keys(screeningAnswers).length === screeningQuestions.length;
      case 7:
        const paymentValid = !!(
          formData.cardNumber && 
          formData.expiryDate && 
          formData.cvv && 
          formData.billingAddress.street && 
          formData.billingAddress.city && 
          formData.billingAddress.postalCode && 
